// ACCORDION AUTO CENTER 
$(".accordion").on("click", function () {
  $("html,body").animate({ scrollTop: $(this).offset().top }, 'smooth');
  return true;
});
// Tabcontent Formation

function openCity(evt, cityName) {
  var i, tabcontent, tablinks;
  tabcontent = document.getElementsByClassName("tabcontent");
  for (i = 0; i < tabcontent.length; i++) {
    tabcontent[i].style.display = "none";
  }
  tablinks = document.getElementsByClassName("tablinks");
  for (i = 0; i < tablinks.length; i++) {
    tablinks[i].className = tablinks[i].className.replace(" active", "");
    booleanValue.style.background = '';
    booleanValue.style.color = '';
    booleanValue1.style.background = '';
    booleanValue1.style.color = '';
    booleanValue2.style.background = '';
    booleanValue2.style.color = '';
    booleanValue3.style.background = '';
    booleanValue3.style.color = '';
    booleanValue4.style.background = '';
    booleanValue4.style.color = '';
  }
  document.getElementById(cityName).style.display = "block";
  evt.currentTarget.className += " active";
}
